CREATE TABLE IF NOT EXISTS Incident ( id INT AUTO_INCREMENT PRIMARY KEY, description TEXT NOT NULL, date DATE NOT NULL CHECK (date >= '1947-11-30'), specific_location TEXT NOT NULL, casualty_info TEXT, casualty_number INT NOT NULL, location ENUM('Gaza', 'West Bank', 'Lebanon', 'Syria', 'Iran', 'Yemen', 'Egypt', 'Jordan', 'Other') NOT NULL );
CREATE TABLE IF NOT EXISTS Coverage ( id INT AUTO_INCREMENT PRIMARY KEY, inc_id INT NOT NULL, publisher text NOT NULL, bias DOUBLE NOT NULL CHECK (bias BETWEEN -1 AND 1), url TEXT NOT NULL, FOREIGN KEY (inc_id) REFERENCES Incident(id) ON DELETE CASCADE );
